mapper.select:

	select post_id, board_id, post_writer, post_title, post_date
	from
		(
		select /*+INDEX_DESC(post post_id_pk)*/
			rownum rn, post_id, board_id, post_writer, post_title, post_date
			from post where rownum <= #{pageNum} * #{amount} and board_id = #{board_id}
		)
	where rn > (#{pageNum} - 1) * #{amount} and board_id = #{board_id}


select ROWNUM, b.board_id, p.post_id, p.post_title, p.post_writer, p.post_date, p.hitcount
	from post p, board b where p.board_id = b.board_id and b.project_id = #{project_id}
	and ROWNUM <= 15 order by p.post_id desc
